import React, { useEffect } from "react";
import MaterialTable from "material-table";
import axios from "axios";

export default function ClientRequests() {
  const [creq, setCreq] = React.useState([]);
  useEffect(() => {
    console.log("here");
    if (creq.length < 1) {
      axios
        .post("/getcreq", { ID: localStorage.getItem("promanID") })
        .then(res => {
          console.log(res);
          setCreq(res.data);
          console.log(res);
        })
        .catch(error => console.log(error));
    }
  });
  const [state, setState] = React.useState({
    columns: [
      { title: "Project Name", field: "projectName" },
      { title: "Company Name", field: "ID" },
      { title: "Client Name", field: "clientName" },
      { title: "Client ID", field: "clientID" }
    ],
    data: [
      creq.forEach(function(item, index) {
        return {
          projectName: item.FullName,
          ID: item.ID,
          clientName: item.OfficeID,
          clientID: item.Key
        };
      })
    ]
  });

  return (
    <MaterialTable
      title="Editable Example"
      columns={state.columns}
      data={state.data}
      editable={{
        onRowAdd: newData =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...state.data];
              data.push(newData);
              setState({ ...state, data });
            }, 600);
          }),
        onRowUpdate: (newData, oldData) =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...state.data];
              data[data.indexOf(oldData)] = newData;
              setState({ ...state, data });
            }, 600);
          }),
        onRowDelete: oldData =>
          new Promise(resolve => {
            setTimeout(() => {
              resolve();
              const data = [...state.data];
              data.splice(data.indexOf(oldData), 1);
              setState({ ...state, data });
            }, 600);
          })
      }}
    />
  );
}
